var crypto = require('crypto');
var async = require('async');
var util = require('util');
var log4js = require("log4js");
var log = log4js.getLogger();

var mongoose = require('lib/mongoose'),
  Schema = mongoose.Schema;

var schema = new Schema({
  username: {
    type: String,
    unique: true,
    required: true
  },
  hashedPassword: {
    type: String,
    required: true
  },
  salt: {
    type: String,
    required: true
  },
  created: {
    type: Date,
    default: Date.now
  },
   money: {
    type: String,
    default: "0"
  }
});

schema.methods.encryptPassword = function(password) {
  return crypto.createHmac('sha1', this.salt).update(password).digest('hex');
};

schema.virtual('password')
  .set(function(password) {
    this._plainPassword = password;
    this.salt = Math.random() + '';
    this.hashedPassword = this.encryptPassword(password);
  })
  .get(function() { return this._plainPassword; });


schema.methods.checkPassword = function(password) {
  return this.encryptPassword(password) === this.hashedPassword;
};

schema.statics.authorize = function(username, password, callback) {
  var User = this;

  async.waterfall([
    function(callback) {
      User.findOne({username: username}, callback);
    },
    function(user, callback) {
      if (user) {
        if (user.checkPassword(password)) {
          callback(null, user);
        } else {
          callback(new AuthError("Пароль неверен"));
        }
      } else {
        var user = new User({username: username, password: password});
        user.save(function(err) {
          if (err) return callback(err);
          callback(null, user);
        });
      }
    }
  ], callback);
};

schema.statics.upMoney = function(id, money_sum, callback) {
  var User = this;
  User.findById(id, function (err, user) {
    if (err) callback(err);
    currentAmount = user.money;
    upedMoney = currentAmount * 1 + money_sum * 1;
    var idObj = {_id: id};
    var user_saved = {};
    user_saved.prototype = user;
    user.money = upedMoney.toString(); 
    log.debug("User schema prepeared for update")
    user.save(function(err){
      if (err) callback(err);
      log.debug("Schema correctly updated");
      User.findById(id, function (err, user) {
        log.info(user);
      });
    });
  });

};

exports.User = mongoose.model('User', schema);


function AuthError(message) {
  Error.apply(this, arguments);
  Error.captureStackTrace(this, AuthError);

  this.message = message;
}

util.inherits(AuthError, Error);

AuthError.prototype.name = 'AuthError';

exports.AuthError = AuthError;


