var ticket = require('models/ticket').Ticket;
var log4js = require('log4js');
var log = log4js.getLogger();

exports.get = function(req, res, next) {
	log.info(global.Ticket.code);
	if(req.query.LMI_PAYMENT_NO == (global.Ticket.code*10)){
		global.Ticket.bill_closed();
		log.debug("Sent Bill_Close Request");
	}
	log.debug("Processed Accept Request");
	res.redirect("/");
};

exports.add_bill = function(req, res, next, code, amount) {
	var Ticket = new ticket(req, res, next, code, amount);
	Ticket.bill_opened();
	global.Ticket = Ticket;
};